import React, { useRef } from "react";
import "../styleComponents/button.css"

const Button = ({ btn_text, icon }) => {

  const btn = useRef();
  const onClick = () => {
    btn.current.style.color = "red"
  }

  return (
      <button ref={btn} onClick={onClick} class="btnn animated-button" >
        <span></span>
        <span></span>
        <span></span>
        <span></span>
        {btn_text} {icon}
      </button>
    );
}

export default Button;
